mapper.select:
select review_id, user_id, storage_id, review_title, review_context, review_write_date, review_edit_date
from tbl_review
where review_id = #{reviewId}

select review_id, u.user_id, storage_id, review_title, review_context, review_write_date, review_edit_date, user_identification, user_password, user_email, user_phone, user_address, user_address_detail, user_gender, user_birth, user_name from tbl_user u join tbl_review r on u.user_id = r.user_id
desc
limit #{page.criteria.startRow}, #{page.criteria.amount}

select review_id, u.user_id, storage_id, review_title, review_context, review_write_date, review_edit_date, user_identification, user_password, user_email, user_phone, user_address, user_address_detail, user_gender, user_birth, user_name
from tbl_user u join tbl_review r on u.user_id = r.user_id
where storage_id = #{storageId}

select count(review_id)
from tbl_review
where storage_id = #{storageId}

select review_id, u.user_id, storage_id, review_title, review_context, review_write_date, review_edit_date, user_identification, user_email, user_phone, user_address, user_address_detail, user_gender, user_birth, user_name from ( select user_id, user_identification, user_email, user_phone, user_address, user_address_detail, user_gender, user_birth, user_name from tbl_user ) u join ( select review_id, user_id, storage_id, review_title, review_context, review_write_date, review_edit_date from tbl_review
) r on u.user_id = r.user_id order by review_id
desc
limit #{page.criteria.startRow}, #{page.criteria.amount}

mapper.update:
update tbl_review
set review_title=#{reviewTitle}, review_context=#{reviewContext}, review_edit_date=CURRENT_TIMESTAMP
where review_id=#{reviewId}

mapper.sql:
where





                    user_id = #{dto.userId}


                    review_title like concat('%', #{dto.keyword}, '%')


                    storage_id = #{dto.storageId}
