mapper.insert:
insert into tbl_user
(user_identification, user_password, user_name, user_email, user_phone, user_address, user_address_detail, user_gender, user_birth)
values(#{userIdentification}, #{userPassword}, #{userName}, #{userEmail}, #{userPhone}, #{userAddress}, #{userAddress}, #{userGender}, #{userBirth});

mapper.select:
select user_id, user_identification, user_password, user_email, user_name, user_phone, user_address, user_address_detail, user_gender, user_birth
from tbl_user
where user_id = #{userId}

select user_id, user_identification, user_password, user_email, user_name, user_phone, user_address, user_address_detail, user_gender, user_birth from tbl_user order by user_id
desc
limit #{page.criteria.startRow}, #{page.criteria.amount}

mapper.update:
update tbl_user
set user_identification=#{userIdentification}, user_password=#{userPassword}, user_email=#{userEmail}, user_name=#{userName},
user_phone=#{userPhone}, user_address=#{userAddress}, user_address_detail=#{userAddressDetail}, user_gender=#{userGender}, user_birth=#{userBirth}
where user_id= #{userId};
